#[derive(Serialize, Deserialize, Clone, Debug, PartialEq)]
/// Represents the data needed to solve a normal Captcha puzzle
///
/// # Example
/// ```
/// # use std::env;
/// use captcha_oxide::{
///     arguments::normal_captcha::{
///         NormalCaptcha,
///         NormalCaptchaMethods
///     },
///     CaptchaSolver,
///     Solution,
/// };
///
/// # #[tokio::main]
/// # pub async fn main() -> Result<(), Box<dyn std::error::Error>> {
/// # dotenv::dotenv();
/// let solver = CaptchaSolver::new("YOUR_API_KEY");
/// # let solver = CaptchaSolver::new(env::var("API_KEY").unwrap());
///
/// let args = NormalCaptcha::builder()
///     .method(NormalCaptchaMethods::Base64("BASE_64_IMAGE".into()))
/// #   .method(NormalCaptchaMethods::Base64("".into()))
///     .text_instructions("Repeat the characters in the image")
/// #   .phrase(true)
/// #   .case_sensitive(true)
///     .build();
///
/// let solution = solver.solve(args).await?.expect("Only None if pingback is set").solution;
/// let Solution::Token(solution) = solution else {
///     unreachable!();
/// };
///
/// # assert_eq!(solution.to_lowercase().replace(' ', ""), "w68hp");
/// assert_ne!(solution, "");
/// # Ok(())
/// # }
/// ```
pub struct NormalCaptcha {
    /// The way you intend on sending the captcha image:
    /// * As multipart/form-data
    /// * Or as a base64 string
    method: NormalCaptchaMethods,

    /// Text will be shown to worker to help him to solve the captcha correctly.
    /// For example: type red symbols only.
    ///
    /// This is required if you don't send `image_instructions`
    text_instructions: Option<String>,

    /// Image with instruction for solving the captcha
    ///
    /// This is required if you don't send `text_instructions`
    image_instructions: Option<ImageInstructions>,

    /// What types of characters are accepted in the answer
    numeric: Option<CharacterRestrictions>,

    /// Callback URL where you wish to receive the response
    pingback: Option<String>,

    /// Whether or not the captcha is case-sensitive
    case_sensitive: Option<bool>,

    /// Whether or not the captcha requires a calculation such as `5 + 3`
    requires_calculation: Option<bool>,

    /// Whether or not the captcha contains more than one word
    phrase: Option<bool>,

    /// Must be in range (1..=20)
    min_len: Option<u8>,

    /// Must be in range (1..=20)
    max_len: Option<u8>,

    /// The alphabet to be used
    language: Option<Language>,

    /// Language code such as `pt-BR`
    language_code: Option<String>,
}

impl NormalCaptcha {
    pub fn builder() -> NormalCaptchaBuilder<
        MethodNotProvided,
        TextInstructionsNotProvided,
        ImgInstructionsNotProvided,
    > {
        NormalCaptchaBuilder::new()
    }
}

impl CaptchaArguments<'_> for NormalCaptcha {
    fn to_request_params(&self, api_key: String) -> Result<Form> {
        let mut request_body = Form::new()
            .text("key", api_key)
            .text("json", "1")
            .text("header_acao", "1")
            .text("soft_id", TWO_CAPTCHA_DEVELOPER_ID);

        match &self.method {
            NormalCaptchaMethods::Base64(data) => {
                if data.is_empty() {
                    panic!(
                        "The data in this enum variant must not be empty. \
                        Make sure you don't use the `Default` trait to fill the `method` field of the \
                        `NormalCaptcha` struct"
                    );
                }

                request_body = request_body
                    .text("method", "base64")
                    .text("body", data.clone());
            }
            NormalCaptchaMethods::Post {
                bytes,
                mime_str,
                file_extension,
            } => {
                let part = Part::bytes((*bytes).clone())
                    .file_name(format!("captcha.{}", (*file_extension).replace('.', "")))
                    .mime_str(mime_str)
                    .map_err(|_| Error::FileParseError)?;

                request_body = request_body.text("method", "post").part("file", part);
            }
        }

        if let Some(ref text_instructions) = self.text_instructions {
            request_body = request_body.text("textinstructions", text_instructions.clone());
        }

        if let Some(ref image_instructions) = self.image_instructions {
            let part = Part::bytes(image_instructions.bytes.clone())
                .file_name(format!(
                    "instructions.{}",
                    image_instructions.extension.replace('.', "")
                ))
                .mime_str(&image_instructions.mime_type)
                .map_err(|_| Error::FileParseError)?;

            request_body = request_body.part("imginstructions", part);
        }

        if let Some(calc) = self.requires_calculation {
            request_body = request_body.text("calc", if calc { "1" } else { "0" });
        }

        if let Some(phrase) = self.phrase {
            request_body = request_body.text("phrase", if phrase { "1" } else { "0" });
        }

        if let Some(case_sensitive) = self.case_sensitive {
            request_body = request_body.text("regsense", if case_sensitive { "1" } else { "0" });
        }

        if let Some(ref lang) = self.language_code {
            request_body = request_body.text("lang", lang.clone());
        }

        if let Some(ref pingback) = self.pingback {
            request_body = request_body.text("pingback", pingback.clone());
        }

        if let Some(min_len) = self.min_len {
            request_body = request_body.text("min_len", min_len.to_string());
        }

        if let Some(max_len) = self.max_len {
            request_body = request_body.text("max_len", max_len.to_string());
        }

        if let Some(language) = &self.language {
            request_body = request_body.text(
                "language",
                match *language {
                    Language::NotSpecified => "0",
                    Language::Cyrillic => "1",
                    Language::Latin => "2",
                },
            );
        }

        if let Some(numeric) = &self.numeric {
            request_body = request_body.text(
                "numeric",
                match *numeric {
                    CharacterRestrictions::NotSpecified => "0",
                    CharacterRestrictions::OnlyNumbers => "1",
                    CharacterRestrictions::OnlyLetters => "2",
                    CharacterRestrictions::OnlyNumbersOrOnlyLetters => "3",
                    CharacterRestrictions::BothNumbersAndLetters => "4",
                },
            );
        }

        Ok(request_body)
    }

    crate::arguments::captcha_arguments::impl_methods!(NormalCaptcha);
}

#[cfg(test)]
mod test {
    use dotenv::dotenv;
    use std::env;

    use crate::{
        arguments::normal_captcha::{NormalCaptcha, NormalCaptchaMethods},
        CaptchaSolver, Solution,
    };

    #[tokio::test]
    #[ignore = "These tests should run all at once, as this will likely cause a 429 block from the 2captcha API"]
    async fn normal_captcha() {
        dotenv().unwrap();
        let solver = CaptchaSolver::new(env::var("API_KEY").unwrap());

        let args = NormalCaptcha::builder()
            .method(NormalCaptchaMethods::Base64("".into()))
            .text_instructions("Repeat the characters in the image")
            .phrase(true)
            .case_sensitive(true)
            .build();

        let solution = solver.solve(args).await;

        assert!(solution.is_ok());

        let solution = solution.unwrap().unwrap().solution;
        let Solution::Token(solution) = solution else {
            unreachable!()
        };

        assert_eq!(solution.to_lowercase().replace(' ', ""), "w68hp");
    }
}
